	/**
	 * Check if mandatory fields are set and updates 'magic fields' with appropriate values
	 * @method beforeSave
	 * @param {array} $value The array of fields
	 * @return {array}
	 * @throws {Exception} If mandatory field is not set
	 */
	function beforeSave($value)
	{
		if (!$this->retrieved) {
			$table = $this->getTable();
			foreach (array('permissions','payload') as $name) {
				if (!isset($value[$name])) {
					throw new Exception("the field $table.$name needs a value, because it is NOT NULL, not auto_increment, and lacks a default value.");
				}
			}
		}						
		// convention: we'll have updatedTime = insertedTime if just created.
		$this->updatedTime = $value['updatedTime'] = new Db_Expression('CURRENT_TIMESTAMP');
		if (!isset($this->fields["id"]) and !isset($value["id"])) {
			$this->id = $value["id"] = "";
		}
		if (!isset($this->fields["publicKeyHash"]) and !isset($value["publicKeyHash"])) {
			$this->publicKeyHash = $value["publicKeyHash"] = "";
		}
		if (!isset($this->fields["permissions"]) and !isset($value["permissions"])) {
			$this->permissions = $value["permissions"] = "";
		}
		if (!isset($this->fields["payload"]) and !isset($value["payload"])) {
			$this->payload = $value["payload"] = "";
		}
		if (!isset($this->fields["signature"]) and !isset($value["signature"])) {
			$this->signature = $value["signature"] = "";
		}
		if (!isset($this->fields["origin"]) and !isset($value["origin"])) {
			$this->origin = $value["origin"] = "";
		}
		return $value;			
	}

	/**
	 * Retrieves field names for class table
	 * @method fieldNames
	 * @static
	 * @param {string} [$table_alias=null] If set, the alieas is added to each field
	 * @param {string} [$field_alias_prefix=null] If set, the method returns associative array of ('prefixed field' => 'field') pairs
	 * @return {array} An array of field names
	 */
	static function fieldNames($table_alias = null, $field_alias_prefix = null)
	{
		$field_names = array('id', 'publicKeyHash', 'userId', 'permissions', 'payload', 'signature', 'secretVersion', 'capability', 'origin', 'appId', 'startTime', 'endTime', 'insertedTime', 'updatedTime');
		$result = $field_names;
		if (!empty($table_alias)) {
			$temp = array();
			foreach ($result as $field_name)
				$temp[] = $table_alias . '.' . $field_name;
			$result = $temp;
		} 
		if (!empty($field_alias_prefix)) {
			$temp = array();
			reset($field_names);
			foreach ($result as $field_name) {
				$temp[$field_alias_prefix . current($field_names)] = $field_name;
				next($field_names);
			}
			$result = $temp;
		}
		return $result;			
	}
